import "core-js/modules/es.array.push.js";
export default {
  props: ["data"],
  data() {
    return {
      pageList: []
    };
  },
  watch: {
    data() {
      this.init();
    }
  },
  mounted() {
    this.init();
  },
  methods: {
    getColor(page) {
      if (page == this.data.nowPage) {
        return "primary";
      }
    },
    movePage(page) {
      this.$emit("movePage", page);
    },
    init() {
      if (this.data) {
        this.pageList = [];
        for (var i = this.data.startNav; i <= this.data.endNav; i++) {
          this.pageList.push(i);
        }
      }
    }
  }
};
